{
  "properties": {
    "displayName": "Create LA workspace in subscription",
    "mode": "Indexed",
    "description": "Create LA workspace in subscription, if not exists",
    "metadata": {
      "version": "1.0.0",
      "category": "OperationalInsights"
    },
    "policyRule": {
      "if": {
          "field": "type",
          "equals": "Microsoft.Compute/virtualMachines"
      },
      "then": {
        "effect": "DeployIfNotExists",
        "details" : {
            "type": "Microsoft.OperationalInsights/workspaces",
            "name": "centrallaworkspace",
            "resourceGroupName": "myrg",
            "evaluationDelay": "PT0M",
            "existenceScope": "ResourceGroup",
            "roleDefinitionIds": [
                "/subscriptions/3f735773-a15a-41bf-bcb9-0217a1a1b07d/providers/Microsoft.Authorization/roleDefinitions/b24988ac-6180-42a0-ab88-20f7382dd24c"
            ],
            "Deployment": {
                "properties" :{
                    "mode": "incremental",
                    "template": {
                        "$schema": "https://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                        "contentVersion": "1.0.0.0",
                        "resources": [
                            {
                                  "type": "Microsoft.OperationalInsights/workspaces",
                                  "apiVersion": "2021-06-01",
                                  "name": "centrallaworkspace",
                                  "location": "centralindia",
                                  "retentionDays": 30,
                                  "sku": {
                                      "name": "Free"
                                  }
                            }
                        ]
                    }
                }
            }

        }
      }
    }
  }
}

