{
  "properties": {
    "displayName": "Enforce encryption on storage Accounts",
    "mode": "Indexed",
    "description": "This policy enables you to enforce encryption on Storage Accounts",
    "metadata": {
      "version": "1.0.0",
      "category": "Storage"
    },
    "policyRule": {
      "if": {
        "allOf": [
          {
            "field": "type",
            "equals": "Microsoft.Storage/storageAccounts"
          },
          {
            "not": {
              "field": "Microsoft.Storage/storageAccounts/encryption.keySource",
              "equals": "Microsoft.Keyvault"
            }
          }
        ]
      },
      "then": {
        "effect": "Deny"
      }
    }
  },
  "name": "sorageAccountIsEncrypted"
}

