{
  "properties": {
    "displayName": "Dsiable Public Access to CosmosDB",
    "mode": "Indexed",
    "description": "This policy disables public access for cosmosdb",
    "metadata": {
      "version": "1.0.0",
      "category": "CosmosDB"
    },
    "policyRule": {
      "if": {
        "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.DocumentDB/databaseAccounts"
                },
                { 
                    "not": { 
                        "allOf" : [
                            {
                                "count": {
                                    "field": "Microsoft.DocumentDB/databaseAccounts/ipRules[*]"
                                },
                                "notEquals": 0

                            },
                            {
                                "count": {
                                  "field": "Microsoft.DocumentDB/databaseAccounts/ipRules[*]",
                                  "where": {
                                    "field": "Microsoft.DocumentDB/databaseAccounts/ipRules[*].ipAddressOrRange",
                                    "equals": "40.40.40.40"
                                  }
                                },
                                "equals": 1
                            } 
                        ]
                    }
                }
        ]
      },
      "then": {
        "effect": "append",
        "details" : [{
                   "field": "Microsoft.DocumentDB/databaseAccounts/ipRules[*]",
                   "value": { 
                       "ipAddressOrRange":  "40.40.40.40"
                    }
               }]
        }
    }
  },
  "name": "DisablePublicAccessOnCosmosDB"
}

{
  "identity": {
    "type": "SystemAssigned"
