{
    "properties": {
        "displayName": "Audit NSGs",
        "description": "Audit NSG's for publicly opened rules i.e 0.0.0.0/0 or *",
        "mode": "Indexed",
        "metadata" :{
            "version": "1.0.0",
            "category": "Network"
        },
        "policyRule": {
            "if": { 
                "count": {
                    "field" : "Microsoft.Network/networkSecurityGroups/securityRules[*]",
                    "where": {
                        "allOf": [
                            {
                                "field" : "Microsoft.Network/networkSecurityGroups/securityRules[*].sourceAddressPrefix",
                                "in" : ["0.0.0.0/0", "*"]
                            },
                            {
                                "field" : "Microsoft.Network/networkSecurityGroups/securityRules[*].access",
                                "equals" : "Allow"
                            },
                            {
                                "field" : "Microsoft.Network/networkSecurityGroups/securityRules[*].direction",
                                "equals" : "Inbound"
                            }
                        ]
                    }
                },
                "greater": 0
            },
            "then": {
                    "effect": "deny"
            }
        }
    }
}

{
  "properties": {
    "displayName": "Enable Retention policy on ACR",
    "mode": "Indexed",
    "description": "Enable Retention policy on ACR",
    "metadata": {
      "version": "1.0.0",
      "category": "Containers"
    },
    "policyRule": {
      "if": {
                "field": "Microsoft.ContainerRegistry/registries/publicNetworkAccess",
                "equals": "enabled"
      },
      "then": {
        "effect": "modify",
        "details": {
            "roleDefinitionIds": [
                "/providers/Microsoft.Authorization/roleDefinitions/b24988ac-6180-42a0-ab88-20f7382dd24c"
            ],
            "operations" : [
                {
                    "operation": "addOrReplace",
                    "field" : "Microsoft.ContainerRegistry/registries/publicNetworkAccess",
                    "value": "disabled"
                }
            ]
        }
      }
    }
  }
}

